using System;
using System.Collections.Generic;
using System.Linq;

class Program
{
    static void Main(string[] args)
    {
        var nmk = Console.ReadLine().Split(' ').Select(int.Parse).ToArray();
        var g = new Graph(nmk[0]);
        for (int i = 0; i < nmk[1]; i++)
        {
            var a = Console.ReadLine().Split(' ').Select(int.Parse).ToArray();
            g.AddEdge(a[0], a[1]);
        }
        var ans = 0;
        for (int i = 0; i < nmk[0]; i++)
        {
            var s = g.GetConnectedComponents(i);
            if (s.Count > 1)
            {
                ans += s.Count - 1;
            }
        }
        Console.WriteLine(ans);
    }

    class Graph
    {
        private readonly int n;
        private readonly List<List<int>> adj;

        public Graph(int n)
        {
            this.n = n;
            this.adj = new List<List<int>>(n);
            for (int i = 0; i < n; i++)
            {
                this.adj.Add(new List<int>());
            }
        }

        public void AddEdge(int u, int v)
        {
            this.adj[u].Add(v);
            this.adj[v].Add(u);
        }

        public IEnumerable<IEnumerable<int>> GetConnectedComponents(int start)
        {
            var visited = new bool[n];
            var queue = new Queue<int>();
            queue.Enqueue(start);
            visited[start] = true;
            var components = new List<IEnumerable<int>>();
            while (queue.Count > 0)
            {
                var u = queue.Dequeue();
                var component = new List<int>();
                component.Add(u);
                for (int v = 0; v < n; v++)
                {
                    if (!visited[v] && this.adj[u].Contains(v))
                    {
                        queue.Enqueue(v);
                        visited[v] = true;
                        component.Add(v);
                    }
                }
                components.Add(component);
            }
            return components;
        }
    }
}
