procedure DFS(u: Integer);
var
  i: Integer;
begin
  used[u] := true;
  for i := 0 to m - 1 do
    if (e[i].u = u) and not used[e[i].v] then
      DFS(e[i].v);
end;

procedure Solve;
var
  i, j, u, v: Integer;
begin
  for i := 0 to n - 1 do
    d[i] := MAXN;
