use std::collections::HashMap;

fn main() {
    let (n, m, k) = read_input();
    let mut graph = HashMap::new();
    for _ in 0..m {
        let (a, b) = read_input();
        graph.entry(a).or_insert(vec![]).push(b);
        graph.entry(b).or_insert(vec![]).push(a);
    }

    let mut provinces = vec![];
    for i in 0..n {
        if !provinces.contains(&i) {
            let mut province = vec![i];
            let mut queue = vec![i];
            while !queue.is_empty() {
                let v = queue.pop().unwrap();
                for &w in &graph[&v] {
                    if !province.contains(&w) {
                        province.push(w);
                        queue.push(w);
                    }
                }
            }
            provinces.push(province);
        }
    }

    let mut tunnels = 0;
    for province in provinces {
        let mut tunnels_in_province = 0;
        for &v in &province {
            tunnels_in_province += graph[&v].len();
        }
        if tunnels_in_province > k {
            tunnels += tunnels_in_province - k;
        }
    }

    println!("{}", tunnels);
}

fn read_input() -> (usize, usize, usize) {
    let mut input = String::new();
    std::io::stdin().read_line(&mut input).unwrap();
    let mut tokens = input.split_whitespace();
    let n = tokens.next().unwrap().parse().unwrap();
    let m = tokens.next().unwrap().parse().unwrap();
    let k = tokens.next().unwrap().parse().unwrap();
    (n, m, k)
}
