        for (int i = 0; i < n; i++) {
            input = br.readLine().split(" ");
            int l = Integer.parseInt(input[0]);
            List<Integer> array = new ArrayList<>();
            for (int j = 1; j <= l; j++) {
                array.add(Integer.parseInt(input[j]));
            }
            arrays.add(array);
        }
        int[] indexes = Arrays.stream(br.readLine().split(" ")).mapToInt(Integer::parseInt).toArray();
        int[] dp = new int[m + 1];
        dp[0] = 0;
        for (int i = 1; i <= m; i++) {
            dp[i] = Integer.MIN_VALUE;
            for (int j = 0; j < n; j++) {
                if (indexes[i - 1] <= arrays.get(j).size()) {
                    dp[i] = Math.max(dp[i], dp[i - arrays.get(j).size()] + arrays.get(j).get(indexes[i - 1] - 1));
                }
            }
        }
