    for (i in 0 until n) {
        for (j in 0 until arr[i].size) {
            dp[i][arr[i][j]] = arr[i][j]
        }
    }

    for (i in 0 until n) {
        for (j in 0 until 10001) {
            for (k in 0 until arr[i].size) {
                dp[i][j + arr[i][k]] = Math.max(dp[i][j + arr[i][k]], dp[i][j] + arr[i][k])
            }
        }
    }

