program FoxCiel;

uses
  SysUtils;

const
  MAXN = 10000;

var
  n, k, l: Integer;
  x: array[0..MAXN] of Integer;
  a: array[0..MAXN] of Integer;
  dp: array[0..MAXN, 0..MAXN] of Integer;

function min(a, b: Integer): Integer;
begin
  if a < b then
    Result := a
  else
    Result := b;
end;

procedure solve;
var
  i, j, k, m: Integer;
begin
  for i := 0 to n do
    for j := 0 to k do
      dp[i, j] := MAXN;
  dp[0, 0] := 0;
  for i := 1 to n do
  begin
    for j := 0 to k do
    begin
      if x[i] = x[i - 1] then
        dp[i, j] := min(dp[i - 1, j], dp[i - 1, j - 1] + 1)
      else
        dp[i, j] := min(dp[i - 1, j], dp[i - 1, j - 1] + 2);
    end;
  end;
  for i := 0 to k do
  begin
    m := 0;
    for j := 1 to l do
    begin
      if a[j] >= x[m] + 1 then
        m := a[j];
      if m >= x[n] then
        break;
    end;
    if m >= x[n] then
      break;
    k := min(k, m - x[m - 1] + 1);
  end;
  if k = 0 then
    Writeln(-1)
  else
    Writeln(dp[n, k]);
end;

begin
  Readln(n, k, l);
  for i := 0 to n do
    Readln(x[i]);
  for i := 0 to l do
    Readln(a[i]);
  solve;
end.
