import java.util.Scanner;

public class Main {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int t = scanner.nextInt();
        int m = scanner.nextInt();
        int[] memory = new int[m];
        for (int i = 0; i < t; i++) {
            String operation = scanner.next();
            if (operation.equals("alloc")) {
                int n = scanner.nextInt();
                if (canAlloc(n, memory)) {
                    for (int j = 0; j < n; j++) {
                        memory[j] = 1;
                    }
                    System.out.println(i + 1);
                } else {
                    System.out.println("NULL");
                }
            } else if (operation.equals("erase")) {
                int x = scanner.nextInt();
                if (memory[x - 1] == 1) {
                    memory[x - 1] = 0;
                } else {
                    System.out.println("ILLEGAL_ERASE_ARGUMENT");
                }
            } else if (operation.equals("defragment")) {
                defragment(memory);
            }
        }
    }

    private static boolean canAlloc(int n, int[] memory) {
        for (int i = 0; i < memory.length - n + 1; i++) {
            if (memory[i] == 0 && memory[i + n - 1] == 0) {
                return true;
            }
        }
        return false;
    }

    private static void defragment(int[] memory) {
        for (int i = 0; i < memory.length; i++) {
            if (memory[i] == 1) {
                for (int j = i + 1; j < memory.length; j++) {
                    if (memory[j] == 0) {
                        memory[i] = 0;
                        memory[j] = 1;
                        break;
                    }
                }
            }
        }
    }
}
