namespace BerlOSMemoryManager
{
    class Program
    {
        static void Main(string[] args)
        {
            string[] input = Console.ReadLine().Split(' ');
            int t = int.Parse(input[0]);
            int m = int.Parse(input[1]);
            int[] memory = new int[m];
            int[] free = new int[m];
            int freeIndex = 0;
            int nextId = 1;
            for (int i = 0; i < t; i++)
            {
                string[] command = Console.ReadLine().Split(' ');
                switch (command[0])
                {
                    case "alloc":
                        int size = int.Parse(command[1]);
                        if (size > m)
                        {
                            Console.WriteLine("NULL");
                        }
                        else
                        {
                            int start = -1;
                            for (int j = 0; j < freeIndex; j++)
                            {
                                if (free[j] >= size)
                                {
                                    start = j;
                                    break;
                                }
                            }
                            if (start == -1)
                            {
                                Console.WriteLine("NULL");
                            }
                            else
                            {
                                for (int j = start; j < start + size; j++)
                                {
                                    memory[j] = nextId;
                                    free[j] = 0;
                                }
                                Console.WriteLine(nextId++);
                            }
                        }
                        break;
                    case "erase":
                        int id = int.Parse(command[1]);
                        if (id <= 0 || id > nextId)
                        {
                            Console.WriteLine("ILLEGAL_ERASE_ARGUMENT");
                        }
                        else
                        {
                            for (int j = 0; j < m; j++)
                            {
                                if (memory[j] == id)
                                {
                                    memory[j] = 0;
                                    free[freeIndex++] = j;
                                    break;
                                }
                            }
                        }
                        break;
                    case "defragment":
                        for (int j = 0; j < freeIndex; j++)
                        {
                            int start = free[j];
                            int end = start;
                            for (int k = j + 1; k < freeIndex; k++)
                            {
                                if (free[k] == end + 1)
                                {
                                    end = free[k];
                                }
                                else
                                {
                                    break;
                                }
                            }
                            for (int k = start; k <= end; k++)
                            {
                                memory[k] = nextId++;
                            }
                        }
                        freeIndex = 0;
                        break;
                }
            }
        }
    }
}
