const free = new Array(m).fill(true);
const alloc = (n) => {
    for (let i = 0; i < m; i++) {
        if (free[i] && memory[i] + n <= m) {
            for (let j = i; j < i + n; j++) {
                memory[j] = 1;
                free[j] = false;
            }
            return i + 1;
        }
    }
    return null;
};
