}

fn read_int() -> usize {
    read_op().parse().unwrap()
}

fn alloc_block(n: usize, memory: &mut Vec<u8>, free_blocks: &mut HashMap<usize, bool>) -> usize {
    let mut block_id = 0;
    for i in 0..memory.len() {
        if free_blocks.get(&i).unwrap() {
            if n <= memory.len() - i {
                block_id = i + 1;
                for j in i..i + n {
                    free_blocks.insert(j, false);
                }
                break;
            }
        }
    }
    block_id
}

fn erase_block(block_id: usize, memory: &mut Vec<u8>, free_blocks: &mut HashMap<usize, bool>) -> bool {
    if block_id <= memory.len() && free_blocks.get(&block_id - 1).unwrap() {
        for i in block_id..block_id + 1 {
            free_blocks.insert(i - 1, true);
        }
        return true;
    }
    false
}

fn defragment(memory: &mut Vec<u8>, free_blocks: &mut HashMap<usize, bool>) {
    let mut free_blocks_vec: Vec<usize> = free_blocks.keys().map(|k| *k).collect();
    free_blocks_vec.sort();
    for i in 0..free_blocks_vec.len() {
        for j in i + 1..free_blocks_vec.len() {
            if free_blocks_vec[i] + 1 == free_blocks_vec[j] {
                free_blocks.insert(free_blocks_vec[i], false);
                free_blocks.insert(free_blocks_vec[j], false);
                free_blocks.insert(free_blocks_vec[i] + 1, true);
            }
        }
    }
}
