        int[] dy = {1, 0, -1, 0};
        Queue<int[]> q = new LinkedList<>();
        q.add(new int[]{0, 0});
        while (!q.isEmpty()) {
            int[] u = q.poll();
            for (int i = 0; i < 4; i++) {
                int x = u[0] + dx[i];
                int y = u[1] + dy[i];
                if (x >= 0 && x < n && y >= 0 && y < m && a[x][y] != k && d[x][y] == -1) {
                    d[x][y] = d[u[0]][u[1]] + 1;
