                        t[cnt++] = a[x][y];
                        x -= dx[d[x][y] - 1];
                        y -= dy[d[x][y] - 1];
                    }
                    int s = 0;
                    for (int l = 0; l < cnt; l++) {
                        s += t[l];
                    }
                    if (ans == -1 || s < ans) {
                        ans = s;
                    }
                }
            }
        }
        if (ans == -1) {
