const
  MaxN = 50;
  MaxM = 50;

type
  TMap = array[0..MaxN, 0..MaxM] of Char;

var
  n, m, k: Integer;
  a: TMap;
  used: array[0..MaxN, 0..MaxM] of Boolean;
  q: array[0..MaxN * MaxM] of Integer;
  front, rear: Integer;
  d: array[0..MaxN * MaxM] of Integer;
  ans: array[0..MaxN * MaxM] of Char;

procedure Init;
var
  i, j: Integer;
begin
  Readln(n, m, k);
  for i := 0 to n - 1 do
  begin
    Readln(a[i]);
    for j := 0 to m - 1 do
      if a[i, j] = 'S' then
        front := i * m + j
      else if a[i, j] = 'T' then
        rear := i * m + j;
  end;
end;

procedure BFS;
var
  i, j, u, v: Integer;
begin
  FillChar(used, SizeOf(used), False);
  FillChar(d, SizeOf(d), MaxN * MaxM);
  d[front] := 0;
  q[0] := front;
  front := 1;
  rear := 1;
  while front < rear do
  begin
    u := q[front];
    front := front + 1;
    for i := -1 to 1 do
      for j := -1 to 1 do
      begin
        v := u + i * m + j;
        if (v >= 0) and (v < n * m) and (not used[v div m, v mod m]) and (a[v div m, v mod m] <> 'S') and (a[v div m, v mod m] <> 'T') then
        begin
          used[v div m, v mod m] := True;
          d[v] := d[u] + 1;
          q[rear] := v;
          rear := rear + 1;
        end;
      end;
  end;
end;

procedure DFS(u: Integer);
var
  i, j: Integer;
begin
  for i := -1 to 1 do
    for j := -1 to 1 do
    begin
      v := u + i * m + j;
      if (v >= 0) and (v < n * m) and (not used[v div m, v mod m]) and (a[v div m, v mod m] <> 'S') and (a[v div m, v mod m] <> 'T') and (d[v] = d[u] + 1) then
      begin
        used[v div m, v mod m] := True;
        ans[d[v]] := a[v div m, v mod m];
        DFS(v);
      end;
    end;
end;

begin
