program GreedyKing;

uses
  SysUtils;

const
  MAXN = 5000;

type
  TPoint = record
    x, y: Integer;
  end;

var
  n, i, j, k: Integer;
  a: array[0..MAXN] of TPoint;
  d: array[0..MAXN, 0..MAXN] of Integer;
  f: array[0..MAXN] of Integer;
  ans: Integer;

procedure ReadInput;
begin
  Readln(n);
  for i := 0 to n - 1 do
  begin
    Readln(a[i].x, a[i].y);
  end;
end;

procedure FloydWarshall;
var
  i, j, k: Integer;
begin
  for i := 0 to n - 1 do
  begin
    for j := 0 to n - 1 do
    begin
      d[i, j] := Abs(a[i].x - a[j].x) + Abs(a[i].y - a[j].y);
    end;
  end;
  for k := 0 to n - 1 do
  begin
    for i := 0 to n - 1 do
    begin
      for j := 0 to n - 1 do
      begin
        d[i, j] := Min(d[i, j], d[i, k] + d[k, j]);
      end;
    end;
  end;
end;

procedure CountArrangements;
var
  i, j, k: Integer;
begin
  for i := 0 to n - 1 do
  begin
    f[i] := 1;
  end;
  for i := 0 to n - 1 do
  begin
    for j := 0 to n - 1 do
    begin
      if (i <> j) and (d[i, j] = d[i, k] + d[k, j]) then
      begin
        f[i] := (f[i] + f[j]) mod 1000000007;
      end;
    end;
  end;
end;

begin
  ReadInput;
  FloydWarshall;
  CountArrangements;
  ans := d[0, n - 1];
  Writeln(ans);
  Writeln(f[0]);
end.
