fn main() {
    let stdin = io::stdin();
    let mut lines = stdin.lock().lines();

    let n = lines.next().unwrap().unwrap().parse::<usize>().unwrap();

    let mut xs = Vec::new();
    let mut ys = Vec::new();

    for _ in 0..n {
        let line = lines.next().unwrap().unwrap();
        let mut iter = line.split_whitespace();
        let x = iter.next().unwrap().parse::<i32>().unwrap();
        let y = iter.next().unwrap().parse::<i32>().unwrap();
        xs.push(x);
        ys.push(y);
    }

    let mut ans = 0;
    let mut cnt = 0;

    for i in 0..n {
        for j in i + 1..n {
            let d = (xs[i] - xs[j]).abs() + (ys[i] - ys[j]).abs();
            ans = max(ans, d);
            cnt += 1;
        }
    }

    println!("{}", ans);
    println!("{}", cnt % 1000000007);
}
""

