    return get_distance(note1, note2) == 3 and get_distance(note2, note3) == 4

if is_major(notes[0], notes[1], notes[2]):
    print('major')
elif is_minor(notes[0], notes[1], notes[2]):
    print('minor')
else:
    print('strange')
