    {
        var input = Console.ReadLine().Split(' ');
        var n = int.Parse(input[0]);
        var m = int.Parse(input[1]);
        var k = int.Parse(input[2]);

        var diamonds = Console.ReadLine().Split(' ').Select(int.Parse).ToList();

        var sum = diamonds.Sum();
        var max = 0;

        for (int i = 0; i < n - 1; i++)
        {
            var diff = diamonds[i + 1] - diamonds[i];
            var newSum = sum - diff;

            var moves = m - (newSum - diamonds[i]) / diff;

            max = Math.Max(max, newSum + moves * diff);
        }

