  N, M, K: Integer;
  A: array[0..MaxN] of TCell;
  Sum: array[0..MaxN] of Integer;
  Ans: Integer;

procedure ReadInput;
var
  I: Integer;
begin
  Readln(N, M, K);
  for I := 0 to N - 1 do
  begin
    Read(A[I].Value);
    A[I].Prev := -1;
  end;
end;

procedure UpdateSum;
var
  I: Integer;
begin
  Sum[0] := 0;
  for I := 1 to N - 1 do
    Sum[I] := Sum[I - 1] + A[I].Value;
end;

procedure Solve;
var
  I, J, L, R: Integer;
  Diff: Integer;
begin
  UpdateSum;
  Ans := 0;
  for I := 0 to N - 1 do
  begin
    L := I;
    R := I;
    while L >= 0 and R < N and A[L].Prev = A[R].Prev do
    begin
      L := A[L].Prev;
      R := A[R].Prev;
    end;
    Diff := Sum[R] - Sum[L];
    if Diff > 0 then
    begin
      Ans := Max(Ans, Diff);
      for J := L + 1 to R - 1 do
      begin
        A[J].Value := A[J].Value - Diff;
      end;
    end;
  end;
end;

begin
  ReadInput;
  Solve;
  Writeln(Ans);
end.
