        }

        int[] c = new int[n + m];

        for (int i = 0; i < n; i++)
        {
            c[i] = a[i];
        }

        for (int i = 0; i < m; i++)
        {
            c[n + i] = b[i];
        }

        int[] d = new int[n + m];

        for (int i = 0; i < n + m; i++)
        {
            d[i] = c[i] - 'a';
        }

        int[] e = new int[n + m];

        for (int i = 0; i < n + m; i++)
        {
            e[i] = d[n - i - 1];
