    for i in 0..n {
        let mut j = 0;
        while dp[i][j] < k {
            j += 1;
        }
        ans[i] = j;
        k -= dp[i][j];
    }
    for i in 0..n {
        print!("{}", ans[i]);
    }
    println!();
}

fn read_line() -> String {
    let mut line = String::new();
    std::io::stdin().read_line(&mut line).unwrap();
    line
}
