    class Program
    {
        static void Main(string[] args)
        {
            string[] input = Console.ReadLine().Split(' ');
            int n = int.Parse(input[0]);
            int m = int.Parse(input[1]);
            char[][] grid = new char[n][];
            for (int i = 0; i < n; i++)
            {
                grid[i] = Console.ReadLine().ToCharArray();
            }
            // 记录每个字符出现的次数
            Dictionary<char, int> count = new Dictionary<char, int>();
            for (int i = 0; i < n; i++)
            {
                for (int j = 0; j < m; j++)
                {
                    char c = grid[i][j];
                    if (count.ContainsKey(c))
                    {
                        count[c]++;
                    }
                    else
                    {
                        count[c] = 1;
                    }
                }
            }
            // 记录每个列字符出现的次数
            Dictionary<char, int> colCount = new Dictionary<char, int>();
            for (int j = 0; j < m; j++)
            {
                for (int i = 0; i < n; i++)
                {
                    char c = grid[i][j];
                    if (colCount.ContainsKey(c))
                    {
                        colCount[c]++;
                    }
                    else
                    {
                        colCount[c] = 1;
                    }
                }
            }
            // 遍历字符串，如果字符在行或列中出现次数大于 1，则删除该字符
            StringBuilder sb = new StringBuilder();
            for (int i = 0; i < n; i++)
            {
                for (int j = 0; j < m; j++)
                {
                    char c = grid[i][j];
                    if (count[c] == 1 && colCount[c] == 1)
                    {
                        sb.Append(c);
                    }
                }
            }
            Console.WriteLine(sb.ToString());
        }
    }
}
