import std.stdio;
import std.algorithm;
import std.array;

struct Edge {
    int from;
    int to;
    int weight;
}

struct Node {
    int id;
    int distance;
    int taxi;
    int taxiCost;
    bool visited;
    Edge[] edges;
}

int main() {
    int n, m;
    scanf(" %d %d ", &n, &m);

    Node[] nodes;
    for (int i = 0; i < n; i++) {
        Node node;
        node.id = i + 1;
        node.distance = -1;
        node.taxi = -1;
        node.taxiCost = -1;
        node.visited = false;
        node.edges = new Edge[0];
        nodes.push(node);
    }

    for (int i = 0; i < m; i++) {
        int u, v, w;
        scanf(" %d %d %d ", &u, &v, &w);

        Edge edge;
        edge.from = u;
        edge.to = v;
        edge.weight = w;

        nodes[u - 1].edges.push(edge);
        nodes[v - 1].edges.push(edge);
    }

    int x, y;
    scanf(" %d %d ", &x, &y);

    int[] taxiDistances = new int[n];
    for (int i = 0; i < n; i++) {
        taxiDistances[i] = -1;
    }

    int[] taxiCosts = new int[n];
    for (int i = 0; i < n; i++) {
        taxiCosts[i] = -1;
    }

    int[] taxiIds = new int[n];
    for (int i = 0; i < n; i++) {
        taxiIds[i] = -1;
    }

    int[] taxiVisited = new bool[n];
    for (int i = 0; i < n; i++) {
        taxiVisited[i] = false;
    }

    int[] taxiQueue = new int[n];
    int queueSize = 0;

    taxiQueue[queueSize++] = x - 1;
    taxiVisited[x - 1] = true;

    while (queueSize > 0) {
        int currentTaxi = taxiQueue[--queueSize];

        for (int i = 0; i < nodes[currentTaxi].edges.length; i++) {
            Edge edge = nodes[currentTaxi].edges[i];

            if (taxiDistances[edge.to] == -1 || taxiDistances[edge.to] > taxiDistances[currentTaxi] + edge.weight) {
                taxiDistances[edge.to] = taxiDistances[currentTaxi] + edge.weight;
                taxiCosts[edge.to] = nodes[currentTaxi].taxiCost + nodes[edge.to].taxiCost;
                taxiIds[edge.to] = nodes[currentTaxi].taxi;

                if (!taxiVisited[edge.to]) {
                    taxiQueue[queueSize++] = edge.to;
                    taxiVisited[edge.to] = true;
                }
            }
        }
    }

    if (taxiDistances[y - 1] == -1) {
        printf(" -1 ");
    } else {
        printf(" %d ", taxiCosts[y - 1]);
    }

    return 0;
}
