    chomp(my $line = <STDIN>);
    ($d, $c) = split(/ /, $line);
    push(@dist, $d);
    push(@cost, $c);
}

for (my $i = 0; $i < $n; $i++) {
    @dist[$i] = -1;
}

@dist[$x] = 0;

while (1) {
    my $min = -1;
    my $min_i = -1;
    for (my $i = 0; $i < $n; $i++) {
        if (@dist[$i] != -1 && (@dist[$i] < $min || $min == -1)) {
            $min = @dist[$i];
            $min_i = $i;
        }
    }
    if ($min_i == -1) {
        last;
    }
    for (my $j = 0; $j < @adj[$min_i]; $j++) {
        my $a = $adj[$min_i][$j][0];
        my $b = $adj[$min_i][$j][1];
