    for _ in 0..m {
        let (u, v, w) = read_ints();
        graph.entry(u).or_insert(vec![]).push((v, w));
        graph.entry(v).or_insert(vec![]).push((u, w));
    }
    let mut dp = vec![vec![-1; n + 1]; n + 1];
    dp[x][0] = 0;
    for i in 0..n {
        for j in 0..n + 1 {
            if dp[i][j] == -1 {
                continue;
            }
            for (v, w) in graph.get(&i).unwrap() {
                if j + w <= n {
                    dp[v][j + w] = max(dp[v][j + w], dp[i][j] + w);
                }
            }
        }
    }
    println!("{}", dp[y][n]);
}

