fn main() {
    let mut input = String::new();
    io::stdin().read_line(&mut input).unwrap();
    let mut input_vec: Vec<i32> = input.split_whitespace().map(|x| x.parse().unwrap()).collect();
    let n = input_vec[0];
    let vb = input_vec[1];
    let vs = input_vec[2];

    let mut input = String::new();
    io::stdin().read_line(&mut input).unwrap();
    let mut input_vec: Vec<i32> = input.split_whitespace().map(|x| x.parse().unwrap()).collect();

    let mut min_time = 1000000000000000000;
    let mut min_index = 0;
    for i in 1..n {
        let time = (input_vec[i] - input_vec[i - 1]) / vb + (xu - input_vec[i]) / vs;
        if time < min_time {
            min_time = time;
            min_index = i;
        }
    }

    println!("{}", min_index);
}
