package main

import (
    "fmt"
    "math"
)

func main() {
    var n int
    fmt.Scanf("%d\n", &n)

    count := 0
    for i := 1; i <= n; i++ {
        if isBinary(i) {
            count++
        }
    }

    fmt.Println(count)
}

func isBinary(n int) bool {
    for n > 0 {
        if n % 10 != 0 && n % 10 != 1 {
            return false
        }
        n /= 10
    }
    return true
}

