fun main() {
    val (n, h) = readLine()!!.split(" ").map { it.toInt() }
    val dp = Array(n + 1) { IntArray(h + 1) { 0 } }
    dp[0][0] = 1
    for (i in 1..n) {
        for (j in 0..h) {
            dp[i][j] = dp[i - 1][j]
            if (j > 0) {
                dp[i][j] += dp[i - 1][j - 1]
            }
        }
    }
    println(dp[n][h])
}
""

